targets.expr:
(
  sum(
      kube_pod_status_ready{namespace="$namespace", condition="true"} 
      * on(pod) group_left(created_by_kind, created_by_name) 
      kube_pod_info{namespace="$namespace"}
  ) by (created_by_kind, created_by_name)
  / 
  sum(
      kube_pod_info{namespace="$namespace"}
  ) by (created_by_kind, created_by_name)
)

(
  sum(
      kube_pod_status_ready{namespace="$namespace", condition="true"} 
      * on(pod) group_left(created_by_name) 
      kube_pod_info{namespace="$namespace",created_by_kind="StatefulSet"}
  ) by (created_by_name)
  / 
  sum(
      kube_pod_info{namespace="$namespace",created_by_kind="StatefulSet"}
  ) by (created_by_name)
)

(
  sum(
      kube_pod_status_ready{namespace="$namespace", condition="true"} 
      * on(pod) group_left(created_by_name) 
      kube_pod_info{namespace="$namespace",created_by_kind="DaemonSet"}
  ) by (created_by_name)
  / 
  sum(
      kube_pod_info{namespace="$namespace",created_by_kind="DaemonSet"}
  ) by (created_by_name)
)
