targets.expr:
sum by (pod, namespace) (
  rate(container_cpu_usage_seconds_total{
    instance="${nodename:raw}",
    pod!="",
    container!="POD",
    container!=""
  }[5m])
) * on(pod, namespace) group_left() (
  kube_pod_status_phase{phase="Running"} == 1
) * 1000

sum by (pod, namespace) (
  container_memory_working_set_bytes{
    instance="${nodename:raw}",
    pod!="",
    container!="POD",
    container!="",
  }
) * on(pod, namespace) group_left() (
  kube_pod_status_phase{phase="Running"} == 1
)

sum by (pod) (
  kube_pod_container_resource_requests{resource="cpu",node="${nodename:raw}"}
  * on(namespace, pod) group_left() 
  kube_pod_status_phase{phase="Running"}
  ) * 1000

sum(
  container_memory_working_set_bytes{instance="${nodename:raw}",container!="POD",container!="" } 
  * on(pod, namespace) group_left() 
  kube_pod_info{node="${nodename:raw}"}
) by (pod)
